.nav-main-item > a:focus, .nav-main-item > a:hover, table.compat-table td, .newsletter-box, .toc, .text-content .card-grid > li, .communitybox, .overheadIndicator, .subnav .toggleable.current, .subnav > ol > li.current, .text-content table.standard-table:not(.learn-box):not(.nostripe) tr:nth-child(2n+1) td, .moreinfo, .text-content .learn-box, #nav-sec > ul > li > a:hover, #nav-sec > ul > li > a:focus, .home-hacks, .light h2, .dark h2, .dashboard-table tbody tr:hover, .callout-box, .text-content table.fullwidth-table th, .revision-list li:nth-child(2n+1)
